mapper.select:
SELECT ID ,PASSWORD ,NAME ,STATE ,GRADE ,EXTENSION_NUMBER
FROM MANAGER
WHERE ID = #{managerId}

SELECT	COUNT(*)
FROM	MANAGER
WHERE	ID = #{managerId} AND PASSWORD = #{password}

SELECT	COUNT(*)
FROM	MANAGER
WHERE	ID = #{managerId}

mapper.update:
    	UPDATE  MANAGER
		   SET  PASSWORD = #{password},
    			NAME = #{name},
    			EXTENSION_NUMBER = #{extensionNumber}
		 WHERE  ID = #{id}

    	UPDATE MANAGER
		SET    STATE = 'C'
		WHERE  ID = #{managerId}
