mapper.update:
UPDATE RESERVATION 
   SET 
		 LAB_NO = #{labNo}
		,START_DATE = to_date(#{startDate},'yyyymmddhh24mi')
		,EXPIRY_DATE = to_date(#{expiryDate},'yyyymmddhh24mi')
		,NOTE = #{note}
		,PLEDGE = #{pledge}
		WHERE NO = #{no}

		UPDATE RESERVATION
		   SET STATE='C'
         WHERE NO =#{no}
